		String str_ = name + " " + 8.0;
		
		System.out.println("name:" + " " + name);
		System.out.println("str:" + " " + str);
		System.out.println("str_:" + " " + str_);
		System.out.println(7 + " ");
		System.out.println(" " + 7);
		System.out.println(7 + "7");
		System.out.println(7 + "");
